source.buildSpec:
{
  "version": "0.2",
  "phases": {
    "install": {
      "runtime-versions": {
        "nodejs": 12
      },
      "commands": [
        "cd backened_pipeline",
        "npm install"
      ]
    },
    "build": {
      "commands": [
        "npm run build",
        "npx cdk synth"
      ]
    }
  },
  "artifacts": {
    "base-directory": "./backened_pipeline/cdk.out",
    "files": [
      "BackenedPipelineStack.template.json"
    ]
  }
}
